classprops.postdata:
eval(
						begin
							PostData.Add('send','');
							PostData.Add('smstext',SMS['Text']);
							PostData.Add('smstoprefix',SMS['Phone_Country'] + SMS['Phone_Def']);
							PostData.Add('smsto',SMS['Phone_Number']);
							PostData.Add('dirtysmstext',SMS['Text']);
							if isAscii(SMS['Text']) then
								PostData.Add('translit','on');
							PostData.Add('confirm_key',HTML.RegExp('"confirm_key" VALUE="(.*?)"',1));
							PostData.Add('confirmcode',SMS['Code']);
							PostData.Add('x','51');
							PostData.Add('y','9');
						end;
					)

classprops.error:
eval(begin
						Result:=Html.RegExp('<center style="margin-bottom: 30">(.*?)</center>',1);
						if Result='' then
							Result:=Html.RegExp('<span class="red"><center><b>(.*?)<',1);
					end;		
					)

classprops.cd_postdata:
eval(
						begin
							PostData.Add('smsid',Html.RegExp('name="smsid" value="(.*?)">',1));
							PostData.Add('x','75');
							PostData.Add('y','10');
						end;
					)

classprops.cd_success:
eval(
						begin
							if Html.ExistsStr('Повідомлення поставлено в чергу') then
								Result:='WAIT'
							else Result:=HTML.ExistsStr('Повідомлення відправлено');
						end;	
					   )
