classprops.postdata:
eval(
						begin
							PostData.Add('dest',SMS['Phone_Number']);
							PostData.Add('id',HTML.RegExp('name="id" value="(.*?)"',1));
							PostData.Add('code',SMS['Code']);
							PostData.Add('smsbody',SMS['Text']);
						end;
					)

classprops.error:
eval(begin
						Result:=HTML.Text;
						if Pos('code error',Result)>0 then
							Result:='Неправильный код потверждения';
					end;	
					)
