classprops.pageurl:
eval(
					begin
						if SMS['Phone_tag']=1 then
							Stored['region']:='buryatia'
						else if SMS['Phone_tag']=2 then
							Stored['region']:='kaliningrad'
						else
							Stored['region']:='msk';
						Result:='http://www.mts.ru/live/smsxtra.aspx?region='+Stored['region'];
					end;
					)

classprops.postdata:
eval(
					var
						DT,DTClient: TDateTime;
						PhoneDef: string;
					begin
						DTClient:=SMS['DeliveryTime'];
						DT:=SMS['DeliveryTime'];
						if SMS['Delivery'] then
						begin	
							if (DT-Now)<5*OneMinute then
							begin
								DT:=DT+5*OneMinute;
								DTClient:=DTClient+5*OneMinute;	
							end;	
						end;
						DT:=DT+(180-TimeZoneBias)*OneMinute;
						DTClient:=DTClient+(180-TimeZoneBias)*OneMinute;
						if SMS['Phone_Def']='495' then
							PhoneDef:='985'
						else PhoneDef:=SMS['Phone_Def'];	
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadScriptManager1_HiddenField',DecodeFormElem(HTML.RegExp('_TSM_CombinedScripts_=(.*?)" type',1)));
						PostData.Add('__EVENTTARGET','');
						PostData.Add('__EVENTARGUMENT','');
						PostData.Add('LBD_VCT_live_smsxtra_ctl00_ctl00_ctl00_smssendcontrol_captcha',Html.RegExp('LBD_VCT_live_smsxtra_ctl00_ctl00_ctl00_smssendcontrol_captcha" value="(.*?)"',1));
						PostData.Add('__VIEWSTATE',Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1));
						PostData.Add('__EVENTVALIDATION',Html.RegExp('id="__EVENTVALIDATION" value="(.*?)"',1));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadFormDecorator1_ClientState','');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ddPrefix',PhoneDef);
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ddPrefix_ClientState','{"logEntries":[],"value":"' + SMS['Phone_Def'] + '","text":"' + SMS['Phone_Def'] + '","enabled":true}');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$txtMsisdn',SMS['Phone_Number']);
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$tbMessage',SMS['Text']);
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$tbLen',Length(SMS['Text']));
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$lblMax',SMS['MaxSymbols']);
						if SMS['Flash'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$chbFlashSMS','on');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$tbCode',SMS['Code']);
						if SMS['Delivery'] then
						begin
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$chbForCalendar','on');
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ucDatePicker$rcbDay',FormatDateTime('dd',DT));
						end;	
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ucDatePicker_rcbDay_ClientState','{"logEntries":[],"value":"'+FormatDateTime('d',DTClient)+'","text":"'+FormatDateTime('d',DTClient)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ucDatePicker$rcbMonth',GetMonthLongRus(DT,true,false));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ucDatePicker_rcbMonth_ClientState','{"logEntries":[],"value":"'+FormatDateTime('m',DTClient)+'","text":"'+GetMonthLongRus(DTClient,true,false)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ucDatePicker$rcbYear',FormatDateTime('yyyy',DT));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ucDatePicker_rcbYear_ClientState','{"logEntries":[],"value":"'+FormatDateTime('yyyy',DTClient)+'","text":"'+FormatDateTime('yyyy',DTClient)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$rcbHour',FormatDateTime('hh',DT));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_rcbHour_ClientState','{"logEntries":[],"value":"'+FormatDateTime('h',DTClient)+'","text":"'+FormatDateTime('h',DTClient)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$rcbMinutes',FormatDateTime('nn',DT));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_rcbMinutes_ClientState','{"logEntries":[],"value":"'+FormatDateTime('n',DTClient)+'","text":"'+FormatDateTime('n',DTClient)+'","enabled":false}');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$btnSend.x','114');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$btnSend.y','22');

					end;
					)

classprops.error:
eval(begin
						if HTML.ExistsStr('smsExtraRedirect.htm') then
							Result:='SMS-сообщение можно отправлять не чаще одного раза в 30 секунд'
						else 	
							Result:=HTML.RegExp('ctl00_ctl00_ctl00_SMSSendControl_lbError">(.*?)</span>',1);
					end;		
							
					)

classprops.cd_postdata:
eval(
						begin
							if HTML.ExistsStr('|updatePanel|') then
							begin
								PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$RadScriptManager1','ctl00$ctl00$ctl00$SMSSendControl$AJAXUpdatePanel|ctl00$ctl00$ctl00$SMSSendControl$StatusLink');
								PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadScriptManager1_HiddenField',Stored['Hidden']);
								PostData.Add('__EVENTTARGET','ctl00$ctl00$ctl00$SMSSendControl$StatusLink');
								PostData.Add('__EVENTARGUMENT',HTML.RegExp('__EVENTARGUMENT\|(.*?)\|',1));
								PostData.Add('__VIEWSTATE',HTML.RegExp('__VIEWSTATE\|(.*?)\|',1));
								PostData.Add('__EVENTVALIDATION',HTML.RegExp('__EVENTVALIDATION\|(.*?)\|',1));
								PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadFormDecorator1_ClientState','');
								PostData.Add('__ASYNCPOST','true');
							end else
							begin
								Stored['Hidden']:=DecodeFormElem(Html.RegExp('_TSM_CombinedScripts_=(.*?)" type',1));
								PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$RadScriptManager1','ctl00$ctl00$ctl00$SMSSendControl$AJAXUpdatePanel|ctl00$ctl00$ctl00$SMSSendControl$StatusLink');
								PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadScriptManager1_HiddenField',Stored['Hidden']);
								PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadFormDecorator1_ClientState','');
								PostData.Add('__EVENTTARGET','ctl00$ctl00$ctl00$SMSSendControl$StatusLink');
								PostData.Add('__EVENTARGUMENT',HTML.RegExp('__EVENTARGUMENT" value="(.*?)"',1));
								PostData.Add('__VIEWSTATE',HTML.RegExp('__VIEWSTATE" value="(.*?)"',1));
								PostData.Add('__EVENTVALIDATION',HTML.RegExp('__EVENTVALIDATION" value="(.*?)"',1));
								PostData.Add('__ASYNCPOST','true');
							end;							
						end;
						)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('cообщение находится в очереди') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('сообщение доставлено');	
							end;
						)

classprops.cd_error:
eval(
						begin
							Result:=HTML.RegExp('cтатус сообщения:<b/>(.*?)<b/>',1);
							if Result='' then
								Result:=HTML.RegExp('style="color:Red;">(.*?)</span>',1);
							if Result='' then
								Result:='Ошибка подтверждения доставки';
						end;
						)
