classprops.postdata:
eval(
						begin
							PostData.Add('MMObjectType','0');
							PostData.Add('isFree','1');
							PostData.Add('MMObjectID','');
							PostData.Add('antispamText',SMS['Code']);
							PostData.Add('textTableRecordId',Html.RegExp('<input name="textTableRecordId" type="hidden" value="(.*?)">',1));
							PostData.Add('Prefix',SMS['Phone_Country']);
							PostData.Add('To',SMS['Phone']);
							PostData.Add('NDC',SMS['Phone_Def']);
							PostData.Add('mobnum',SMS['Phone_Number']);
							PostData.Add('Msg',SMS['Text']);
							PostData.Add('count',Length(SMS['Text']));
							PostData.Add('Hour',FormatDateTime('hh',SMS['DeliveryTime']));
							PostData.Add('Min',FormatDateTime('nn',SMS['DeliveryTime']));
							PostData.Add('Day',FormatDateTime('dd',SMS['DeliveryTime']));
							PostData.Add('Mon',FormatDateTime('mm',SMS['DeliveryTime']));
							PostData.Add('Year',FormatDateTime('yyyy',SMS['DeliveryTime']));
						end;
					)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('Ваше сообщение в очереди') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Ваше сообщение доставлено');
							end;
						)
