classprops.postdata:
eval(
						begin
							PostData.Add('LBD_VCT_freesendwithlinks_captcha',Html.RegExp('id="LBD_VCT_freesendwithlinks_captcha" value="(.*?)"',1));
							PostData.Add('__EVENTTARGET',Html.RegExp('id="__EVENTTARGET" value="(.*?)"',1));
							PostData.Add('__EVENTARGUMENT',Html.RegExp('id="__EVENTARGUMENT" value="(.*?)"',1));
							PostData.Add('__VIEWSTATE',Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1));
							PostData.Add('__EVENTVALIDATION',Html.RegExp('id="__EVENTVALIDATION" value="(.*?)"',1));
							PostData.Add('currentID','-1');
							PostData.Add('ddlDefCode',MMS['Phone_Def']);
							PostData.Add('tbPhone',MMS['Phone_Number']);
							PostData.Add('ddlTopic','0');
							PostData.Add('tbTopic',MMS['Subject']);
							PostData.Add('tbMessage',MMS['Text']);
							PostData.AddData('fileUpload',MMS['Multimedia']);
							PostData.Add('tbCode',MMS['Code']);
							PostData.Add('btnSend.x','140');
							PostData.Add('btnSend.y','33');
						end;
					)

classprops.cd_posturl:
eval(begin
							Result:='http://www.mts.ru/checkStatusFreeWithLinks.aspx';
							if HTML.ExistsStr('|updatePanel|') then
								Result:=Result + HTML.RegExp('\|checkStatusFreeWithLinks.aspx(.*?)\|',1)
							else	
								Result:=Result+Html.RegExp('checkStatusFreeWithLinks\.aspx(.*?)"',1);
						end;
							)

classprops.cd_postdata:
eval(
						begin
							PostData.Add('scriptManagerId','AJAXUpdatePanel|StatusLink');
							PostData.Add('__EVENTTARGET','StatusLink');
							PostData.Add('__EVENTARGUMENT','');
							if HTML.ExistsStr('|updatePanel|') then
							begin
								PostData.Add('__VIEWSTATE',HTML.RegExp('__VIEWSTATE\|(.*?)\|',1));
								PostData.Add('__EVENTVALIDATION',HTML.RegExp('__EVENTVALIDATION\|(.*?)\|',1));
							end else
							begin
								PostData.Add('__VIEWSTATE',Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1));
								PostData.Add('__EVENTVALIDATION',Html.RegExp('id="__EVENTVALIDATION" value="(.*?)"',1));
							end;	
							PostData.Add('__ASYNCPOST','true');
						end;
					)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('В очереди.') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Доставлено.');
							end;
						)
