classprops.postdata:
eval(
			begin
				PostData.Add('phone',SMS['Phone_Number']);
				PostData.Add('message',SMS['Text']);
				PostData.Add('message_length',Length(SMS['Text']));
				PostData.Add('hash',Html.RegExp('name="hash" value="(.*?)"',1));
				PostData.Add('code',SMS['Code']);
				PostData.Add('x','10');
				PostData.Add('y','14');
			end;
		)

classprops.cd_success:
eval(
						begin
							if HTML.ExistsStr('Ваше сообщение помещено в очередь на отправку') then
								Result:='WAIT'
							else Result:=HTML.ExistsStr('Ваше сообщение успешно отправлено');
						end;	
						)
