classprops.postdata:
eval(
			begin
				PostData.Add('phone_number',SMS['Phone_Def']+SMS['Phone_Number']);
				PostData.Add('msg',SMS['Text']);
				PostData.Add('sender','');
				if SMS['Delivery'] then
				begin
					PostData.Add('day',FormatDateTime('dd',SMS['DeliveryTime']));
					PostData.Add('month',FormatDateTime('mm',SMS['DeliveryTime']));
					PostData.Add('hours',FormatDateTime('hh',SMS['DeliveryTime']));
					PostData.Add('minutes',FormatDateTime('nn',SMS['DeliveryTime']));
				end else
				begin
					PostData.Add('day','');
					PostData.Add('month','');
					PostData.Add('hours','');
					PostData.Add('minutes','');
				end;	
				PostData.Add('passwd',SMS['Code']);
			end;
		)
