classprops.postdata:
eval(
			begin
				PostData.Add('codekey',HTML.RegExp('name="codekey".*?value="(.*?)"',1));
				PostData.Add('charcheck',HTML.RegExp('name="charcheck".*?value="(.*?)"',1));
				PostData.Add('lang',HTML.RegExp('name="lang".*?value="(.*?)"',1));
				PostData.Add('codevalue',SMS['Code']);
				PostData.Add('prefix',SMS['Phone_Country']+SMS['Phone_Def']);
				PostData.Add('addr',SMS['Phone_Number']);
				PostData.Add('message',SMS['Text']);
				PostData.Add('messlen',SMS['LeaveSymbols']);
				PostData.Add('go','Отправить сейчас');
				if (SMS['Delivery']) then
					PostData.Add('send_after_flag','on');
				PostData.Add('send_day',FormatDateTime('dd',SMS['DeliveryTime']));
				PostData.Add('send_month',FormatDateTime('mm',SMS['DeliveryTime']));
				PostData.Add('send_hour',FormatDateTime('hh',SMS['DeliveryTime']));
				PostData.Add('send_minute',FormatDateTime('nn',SMS['DeliveryTime']));
				PostData.Add('send_year',FormatDateTime('yyyy',SMS['DeliveryTime']));
			end;
		)

classprops.error:
eval(
				begin
					if Pos('/invalid_key',HTTP['Location'])>0 then
						Result:='Неверный код'
					else if Pos('/min_time_interval',HTTP['Location'])>0 then
						Result:='Сообщение можно отправлять только через 60 секунд после предыдущего'
					else Result:='Неизвестная ошибка';
				end;		
			)

classprops.cd_url:
eval(
					begin
						if HTTP.Exists('Location') then
							Result:='http://sms.megafonmoscow.ru/send/status/'+ StrBetween(HTTP['Location'],'/send/','')
						else
							Result:=HTTP['URL'];
					end;
					)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('Сообщение в процессе доставки') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Сообщение доставлено');
							end;
						)
