classprops.postdata:
eval(
						begin
							PostData.Add('prefix',SMS['Phone_Def']);
							PostData.Add('msisdn',SMS['Phone_Number']);
							PostData.Add('symbols_num',iff(isAscii(SMS['Text']),GateInfo['MaxSymbols'],GateInfo['MaxUnicodeSymbols'])-Length(SMS['Text']));
							PostData.Add('message',SMS['Text']);
							if not isAscii(SMS['Text']) then PostData.Add('russian','on');
							if SMS['Flash'] then PostData.Add('show','on');
							PostData.Add('sms_day',FormatDateTime('dd',SMS['DeliveryTime']));
							PostData.Add('sms_month',FormatDateTime('mm',SMS['DeliveryTime']));
							PostData.Add('sms_year',FormatDateTime('yy',SMS['DeliveryTime']));
							PostData.Add('sms_hour',FormatDateTime('hh',SMS['DeliveryTime']));
							PostData.Add('sms_minute',FormatDateTime('nn',SMS['DeliveryTime']));
							PostData.Add('code',SMS['Code']);
						end;
					)
