classprops.mu_postdata:
eval(
						var
							MC: TMMSContentData;
						begin
							PostData.Add('owner_site_id',Html.RegExp('name="owner_site_id" value="(.*?)"',1));
							PostData.Add('control_id',Html.RegExp('name="control_id" value="(.*?)"',1));
							PostData.Add('owner_site_hash',Html.RegExp('name="owner_site_hash" value="(.*?)"',1));
							MC:=TMMSContentData(MMS['MultiMedia']);
							if MC['Type']=mtImage then
								PostData.Add('action','add_img')
							else
								PostData.Add('action','add_snd');
							PostData.Add('mode','');
							PostData.Add('page','');
							PostData.Add('delay_time','');
							PostData.Add('offset_time','');
							PostData.Add('message_text_trans','');
							PostData.Add('subsc_pwd','');
							PostData.Add('phone_prefix','');
							PostData.Add('phone_number_list','');
							PostData.Add('images','');
							PostData.Add('sounds','');
							PostData.Add('user_img','');
							PostData.Add('user_snd','');
							PostData.Add('confirm_code',Html.RegExp('name="confirm_code" value="(.*?)"',1));
							PostData.Add('phone_number','');
							PostData.Add('message_text','');
							if MMS['Delivery'] then
								PostData.Add('delay_send','1');
							PostData.Add('send_hour',FormatDateTime('h',MMS['DeliveryTime']));
							PostData.Add('send_minute',FormatDateTime('n',MMS['DeliveryTime']));
							PostData.Add('send_day',FormatDateTime('d',MMS['DeliveryTime']));
							PostData.Add('send_month',FormatDateTime('m',MMS['DeliveryTime'])-1);
							PostData.Add('send_year',FormatDateTime('yyyy',MMS['DeliveryTime']));
							PostData.Add('subsc_name','');
							PostData.Add('subsc_passwd','');
							PostData.Add('captcha_code','');
							if MC['Type']=mtAudio then
								PostData.AddData('sound_path',MMS['MultiMedia'])
							else
								PostData.AddData('sound_path','');
							PostData.Add('melodies','0');
							if MC['Type']=mtImage then
								PostData.AddData('image_path',MMS['MultiMedia'])
							else
								PostData.AddData('image_path','');
						end;
						(

classprops.postdata:
eval(
						var
							MC: TMMSContentData;
						begin
							PostData.Add('owner_site_id',Html.RegExp('name="owner_site_id" value="(.*?)"',1));
							PostData.Add('control_id',Html.RegExp('name="control_id" value="(.*?)"',1));
							PostData.Add('owner_site_hash',Html.RegExp('name="owner_site_hash" value="(.*?)"',1));
							MC:=TMMSContentData(MMS['MultiMedia']);
							PostData.Add('action','ins');
							PostData.Add('mode','insMms');
							PostData.Add('page','');
							if MMS['Delivery'] then
								PostData.Add('delay_time',(DateTimeToUnix(MMS['DeliveryTime'])-DateTimeToUnix(Now))*1000)
							else
								PostData.Add('delay_time','');
							PostData.Add('offset_time',-TimeZoneBias);
							PostData.Add('message_text_trans','');
							PostData.Add('subsc_pwd','');
							PostData.Add('phone_prefix','');
							PostData.Add('phone_number_list','');
							PostData.Add('images',iff(MC['Type']=mtImage,MC['FileName'],''));
							PostData.Add('sounds',iff(MC['Type']=mtAudio,MC['FileName'],''));
							PostData.Add('user_img',iff(MC['Type']=mtImage,'1',''));
							PostData.Add('user_snd',iff(MC['Type']=mtAudio,'1',''));
							PostData.Add('confirm_code',Html.RegExp('name="confirm_code" value="(.*?)"',1));
							PostData.Add('phone_number',MMS['Phone']);
							PostData.Add('message_text',MMS['Text']);
							if MMS['Delivery'] then
								PostData.Add('delay_send','1');
							PostData.Add('send_hour',FormatDateTime('h',MMS['DeliveryTime']));
							PostData.Add('send_minute',FormatDateTime('n',MMS['DeliveryTime']));
							PostData.Add('send_day',FormatDateTime('d',MMS['DeliveryTime']));
							PostData.Add('send_month',FormatDateTime('m',MMS['DeliveryTime'])-1);
							PostData.Add('send_year',FormatDateTime('yyyy',MMS['DeliveryTime']));
							PostData.Add('subsc_name','');
							PostData.Add('subsc_passwd','');
							PostData.Add('captcha_code',MMS['Code']);
							PostData.AddData('sound_path','');
							PostData.Add('melodies','0');
							PostData.AddData('image_path','');
						end;
					)

classprops.error:
eval(begin
						Result:=Html.RegExp('<script type="text/javascript">alert\("(.*?)"\);</script>',1);
						if Result='' then
							Result:=Html.RegExp('<br/><font color="red">(.*?)<br/></font><',1);
					end;	
					)
