classprops.postdata:
eval(
			begin
				PostData.Add('action','send_sms');
				PostData.Add('smstype','SENDSMS');
				PostData.Add('sender','-SMS-');
				PostData.Add('post_phone','+'+SMS['Phone']);
				PostData.Add('mess'+Html.RegExp('<textarea name="mess(.*?)"',1),SMS['Text']);
				PostData.Add('secret_code',SMS['Code']);
				PostData.Add('x','68');
				PostData.Add('y','9');
			end;
		)

classprops.cd_success:
eval(
						begin
							if Html.ExistsStr('В очереди отправки') then
								Result:='WAIT'
							else Result:=Html.ExistsStr('Передано оператору');
						end;
					)
