classprops.postdata:
eval(
						begin
							PostData.Add('utf8','%E2%9C%93');
							PostData.Add('authenticity_token',Html.RegExp('name="authenticity_token" type="hidden" value="(.*?)"', 1));
							PostData.Add('short_message[code]',SMS['Phone_Def']);
							PostData.Add('short_message[phone_number]',SMS['Phone_Number']);
							if isAscii(SMS['Text']) then
								PostData.Add('short_message[translit]','true');
							PostData.Add('short_message[body]',SMS['Text']);
							PostData.Add('recaptcha_challenge_field',Stored['challenge']);
							PostData.Add('recaptcha_response_field',SMS['Code']);
						end;
					)
