classprops.postdata:
eval(
					begin
						PostData.UnsafeFormName:='<>"%{}|\^~`'+NonAsciiChar;
						PostData.Add('submitted[recepient_mobile_phone_number]',SMS['Phone_Def']+SMS['Phone_Number']);
						PostData.Add('submitted[message]',SMS['Text']);
						PostData.Add('details[sid]','');
						PostData.Add('details[page_num]','1');
						PostData.Add('details[page_count]','1');
						PostData.Add('details[finished]','0');
						PostData.Add('form_build_id',Html.RegExp('name="form_build_id" value="(.*?)"',1));
						PostData.Add('form_id','webform_client_form_' + Html.RegExp('value="webform_client_form_(.*?)"',1));
						PostData.Add('tabmod[tabmod__active_tab]','');
						PostData.Add('captcha_sid',Html.RegExp('name="captcha_sid" value="(.*?)"',1));
						PostData.Add('captcha_token',Html.RegExp('name="captcha_token" value="(.*?)"',1));
						PostData.Add('captcha_response',SMS['Code']);
						PostData.Add('op','ОТПРАВИТЬ');
					end;
					)

classprops.error:
eval(
					begin
						if Html.ExistsStr('captcha_response.*?class="form-text required error') then
							Result:='Неверно введены символы с картинки.'
						else if Html.ExistsStr('bite_error_number') then
							Result:='Недействительный номер телефона. Убедись, что номер телефона введён правильно и что он принадлежит клиенту "BITE Latvija"'
						else
							Result:='Сообщение не отправлено';
					end;
					)
