classprops.postdata:
eval(
					begin
						PostData.UnsafeFormName:='<>"%{}|\^~`'+NonAsciiChar;
						PostData.Add('SentmmsForm[prefix]',Stored['Prefix_'+MMS['Phone_Def']]);
						PostData.Add('SentmmsForm[to]',MMS['Phone_Number']);
						PostData.Add('SentmmsForm[title]',MMS['Subject']);
						PostData.Add('SentmmsForm[message]',MMS['Text']);
						PostData.Add('SentmmsForm[name]','');
						PostData.Add('SentmmsForm[fromPhone]','');
						PostData.Add('SentmmsForm[file_import0]','');
						PostData.AddData('SentmmsForm[file_import0]',MMS['Multimedia']);
						PostData.Add('SentmmsForm[file_preview]','');
						PostData.Add('SentmmsForm[Catalog]','');
						PostData.Add('SentmmsForm[hurlImage]','');
						PostData.Add('SentmmsForm[file_upload]','');
						PostData.Add('Sent','Отправить');
						PostData.Add('SentmmsForm[urlImage]','');
						PostData.Add('SentmmsForm[verifyCode]',MMS['Code']);
					end;
					)

classprops.error:
eval(begin
			Result:=Html.Regexp('Пожалуйста исправьте следующие ошибки ввода:.*?<li>(.*?)</li>',1);
			if Result='' then
			begin
				if Html.ExistsStr('Число MMS, отправляемых анонимно, ограничено в целях безопасности абонентов') then
					Result:='Превышен лимит на количество MMS-сообщений, попробуйте позже'
				else
					Result:='Неизвестная ошибка';
			end;		
			end;)
