classprops.sendinit:
eval(begin
							if SMS['Delivery'] then
							begin
								Stored['Site']:='www';
								Stored['imgRegExp']:='imgcode.php\?(.*?)"';
								Stored['img']:='imgcode.php';
							end else
							begin
								Stored['Site']:='wap';
								Stored['imgRegExp']:='wapcode.php\?(.*?)"';
								Stored['img']:='wapcode.php';
							end;
						end;
					)

classprops.postdata:
eval(
			var
				DT: TDateTime;
			begin
				DT:=AdjustTimeZone(SMS['DeliveryTime'],180,true);
				
				PostData.Add('type','0');
				PostData.Add('addr',SMS['Phone']);
				PostData.Add('imgrnd',Html.RegExp(Stored['imgRegExp'],1));
				PostData.Add('imgcode',SMS['Code']);
				PostData.Add('text',SMS['Text']);
				
				if SMS['Delivery'] then
				begin
					if SMS['Flash'] then
						PostData.Add('fl','on');
					PostData.Add('_tr','');
					PostData.Add('tt','0');
					PostData.Add('dtonoff','on');
					PostData.Add('dtday',FormatDateTime('dd',DT));
					PostData.Add('dtmon',FormatDateTime('m',DT));
					PostData.Add('dtyear',FormatDateTime('yyyy',DT));
					PostData.Add('dttm',FormatDateTime('hh:nn',DT));
				end else
				begin
					if SMS['Flash'] then
						PostData.Add('fl[1]','on');
					PostData.Add('fl[4]','on');
				end;	
			end;
		)

classprops.success:
eval(begin
				Result:=Html.ExistsStr('Cooбщeниe пpинятo к дocтaвкe') or
						Html.ExistsStr('Сообщение поставлено в очередь на отправку') or
						Html.ExistsStr('Oжидaeт oтпpaвки') or
						Html.ExistsStr('Пepeдaнo oпepaтopy') or
						Html.ExistsStr('Дocтaвлeнo');
				Stored['MsgID']:=Html.RegExp('status/\?(.*?)''',1);		
			 	end;	
				)

classprops.error:
eval(begin
					if SMS['Delivery'] then
						Result:=Html.RegExp('<img src=/im/error\.gif width=16><td><b>(.*?)</b>',1)
					else
						Result:=Html.RegExp('<b class="r">(.*?)</b>',1);
					end;

classprops.cd_success:
eval(
						begin
							if Html.ExistsStr('Oжидaeт oтпpaвки') or
								Html.ExistsStr('Пepeдaнo oпepaтopy')
								then
									Result:='WAIT'
							else Result:=Html.ExistsStr('Дocтaвлeнo');
						end;
					)

classprops.cd_error:
eval(
					begin
						Result:='Ошибка подтверждения доставки';
					end;
