classprops.postdata:
eval(
				begin
					PostData.Add('tll','+'+SMS['Phone']);					
					PostData.Add('co','on');
					PostData.Add('submitted','yes');
					PostData.Add('txt',SMS['Text']);
					PostData.Add('send','send');
				end;)

classprops.error:
eval(begin
				if Html.ExistsStr('Вы уже отправили 10 сообщений сегодня.') then
					Result:='Превышен лимит в 10 сообщений в сутки'
				else	
					Result:=Html.RegExp('<h3 style="Color: red;">(.*?)</h3>',1);
			end;)
			)
