classprops.pageurl:
eval(
					begin
						if SMS['Phone_tag']=1 then
						begin
							Stored['siteurl']:='http://sms.buryatia.mts.ru/';
							Stored['timeadd']:=-4;
						end	
						else if SMS['Phone_tag']=2 then
						begin
							Stored['siteurl']:='http://sms.kaliningrad.mts.ru/';
							Stored['timeadd']:=2;
						end	
						else
						begin
							Stored['siteurl']:='http://www.mts.ru/messaging/sendsms/';
							Stored['timeadd']:=0;
						end;
						Result:=Stored['siteurl'];
					end;
					)

classprops.postdata:
eval(
					var
						DT: TDateTime;
						PhoneDef: string;
					begin
						DT:=SMS['DeliveryTime'];
						if SMS['Delivery'] then
						begin	
							if (DT-Now)<5*OneMinute then
							begin
								DT:=DT+5*OneMinute;
							end;	
						end;
						DT:=AdjustTimeZone(DT,180,true);
						if not SMS['Delivery'] then
							DT:=DT+Stored['timeadd']*OneHour;
						if SMS['Phone_Def']='495' then
							PhoneDef:='985'
						else PhoneDef:=SMS['Phone_Def'];

						Stored['captchanewkey']:=Html.RegExp('smssendingcontrol_captchanew" value="(.*?)"',1);
						Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);
						Stored['RegionSiteId']:=Html.RegExp('name="RegionSiteId" value="(.*?)"',1);
						Stored['TopMenuParentId']:=Html.RegExp('name="TopMenuParentId" value="(.*?)"',1);
						Stored['BannerId']:=Html.RegExp('name="BannerId" value="(.*?)"',1);

						PostData.Add('ctl00$ctl03$ctl00$scriptManagerId','ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$updatePanelPanel|ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$btnSend');
						PostData.Add('ctl00_ctl03_ctl00_scriptManagerId_TSM',';;System.Web.Extensions, Version=3.5.0.0, Culture=neutral, PublicKeyToken=31bf3856ad364e35:ru-RU:d0c4ca6e-6b5d-49b6-922d-5244924fb100:ea597d4b:b25378d2;Telerik.Web.UI, Version=2010.2.826.20, Culture=neutral, PublicKeyToken=121fae78165ba3d4:ru-RU:d89f6e67-3ab0-4fcf-b5f1-b51b735ae3d9:16e4e7cd:86526ba7:ed16cbdc:f7645509:24ee1bba:1e771326:aa288e2d');
						PostData.Add('__EVENTTARGET','');
						PostData.Add('__EVENTARGUMENT','');
						PostData.Add('LBD_VCT_live_smssending_ctl00_ctl03_ctl00_ctl04_ctl00_ctl00_ctl00_ctl00_ctl01_ctl00_ctl01_ctl00_smssendingcontrol_captchanew',Stored['captchanewkey']);
						PostData.Add('__VIEWSTATE',Stored['ViewState']);
						PostData.Add('ctl00_ctl03_ctl00_rfdFormDecorator_ClientState','');
						PostData.Add('ctl00_ctl03_ctl00_ctl04_ctl00_ctl00_ctl00_ctl00_ctl01_ctl00_ctl01_ctl00_SmsSendingControl_RadFormDecorator1_ClientState','');
						PostData.Add('ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ddPrefix', SMS['Phone_Def']);
						PostData.Add('ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$tbPhoneNumber', SMS['Phone_Number']);
						PostData.Add('ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$tbMessage',SMS['Text']);
						PostData.Add('ctl00$ctl03$ctl00$ctl04$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$tbCode',SMS['Code']);
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl03$ctl00$ctl03$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$chbForCalendar','on');
						PostData.Add('ctl00$ctl03$ctl00$ctl03$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ctl01',FormatDateTime('dd',DT));
						PostData.Add('ctl00$ctl03$ctl00$ctl03$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ctl02',GetMonthLongRus(DT,false,true));
						PostData.Add('ctl00$ctl03$ctl00$ctl03$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ctl03',FormatDateTime('yyyy',DT));
						PostData.Add('ctl00$ctl03$ctl00$ctl03$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ctl04',FormatDateTime('hh',DT));
						PostData.Add('ctl00$ctl03$ctl00$ctl03$ctl00$ctl00$ctl00$ctl00$ctl01$ctl00$ctl01$ctl00$SmsSendingControl$ctl06',FormatDateTime('nn',DT));
