classprops.postdata:
eval(
					var
						DT: TDateTime;
						PhoneDef: string;
					begin
						DT:=SMS['DeliveryTime'];
						if SMS['Delivery'] then
						begin	
							if (DT-Now)<5*OneMinute then
							begin
								DT:=DT+5*OneMinute;
							end;	
						end;
						DT:=AdjustTimeZone(DT,180,true);
						if SMS['Phone_Def']='495' then
							PhoneDef:='985'
						else PhoneDef:=SMS['Phone_Def'];

						Stored['captchanewkey']:=Html.RegExp('smssendingcontrol_captchanew" value="(.*?)"',1);
						Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);

						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$scriptManagerId','ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$updatePanelPanel|ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$btnSend');
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_scriptManagerId_TSM',';;System.Web.Extensions, Version=4.0.0.0, Culture=neutral, PublicKeyToken=31bf3856ad364e35:ru-RU:1f68db6e-ab92-4c56-8744-13e09bf43565:ea597d4b:b25378d2;Telerik.Web.UI, Version=2010.2.929.40, Culture=neutral, PublicKeyToken=121fae78165ba3d4:ru-RU:183fb741-216d-4765-9b46-4a1f5d38fdd7:16e4e7cd:86526ba7:ed16cbdc:f7645509:24ee1bba:1e771326:aa288e2d');
						PostData.Add('__EVENTTARGET','');
						PostData.Add('__EVENTARGUMENT','');
						PostData.Add('LBD_VCT_live_nd_smssending_ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_ctl01_ctl00_smssendingcontrol_captchanew',Stored['captchanewkey']);
						PostData.Add('__VIEWSTATE',Stored['ViewState']);
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_rfdFormDecorator_ClientState','');
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_ctl01_ctl00_SmsSendingControl_RadFormDecorator1_ClientState','');
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$tbPhoneNumber',SMS['Phone_Def']+SMS['Phone_Number']);
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$tbMessage',SMS['Text']);
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$tbCode',SMS['Code']);
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$chbForCalendar','on');
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$ctl01',FormatDateTime('dd',DT));
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_ctl01_ctl00_SmsSendingControl_ctl01_ClientState','{"logEntries":[],"value":"'+FormatDateTime('d',DT)+'","text":"'+FormatDateTime('dd',DT)+'","enabled":false}');
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$ctl02',GetMonthLongRus(DT,false,true));
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_ctl01_ctl00_SmsSendingControl_ctl02_ClientState','{"logEntries":[],"value":"'+FormatDateTime('m',DT)+'","text":"'+GetMonthLongRus(DT,false,true)+'","enabled":false}');
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$ctl03',FormatDateTime('yyyy',DT));
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_ctl01_ctl00_SmsSendingControl_ctl03_ClientState','{"logEntries":[],"value":"'+FormatDateTime('yyyy',DT)+'","text":"'+FormatDateTime('yyyy',DT)+'","enabled":false}');
						PostData.Add('ctl00$ctl00$ctl00$ctl03$ctl00$ctl05$ctl01$ctl01$ctl00$SmsSendingControl$ctl04',FormatDateTime('hh',DT));
						PostData.Add('ctl00_ctl00_ctl00_ctl03_ctl00_ctl05_ctl01_ctl01_ctl00_SmsSendingControl_ctl04_ClientState','{"logEntries":[],"value":"'+FormatDateTime('h',DT)+'","text":"'+FormatDateTime('hh',DT)+'","enabled":false}');
