classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('сообщение находится в очереди на доставку') or
									HTML.ExistsStr('ожидаем') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr(' сообщение отправлено') or HTML.ExistsStr('сообщение доставлено');	
							end;
						)

classprops.cd_error:
eval(
						begin
							Result:=HTML.RegExp('cтатус сообщения:<b/>(.*?)<b/>',1);
							if Result='' then
								Result:=HTML.RegExp('style="color:Red;">(.*?)</span>',1);
							if Result='' then
								Result:=Html.RegExp('<span id="ctl00_ctl00_ctl00_SMSSendControl_ResultId" class="left_align"><b/>(.*?)<b/>',1);
							if Result='' then
								Result:=Html.RegExp('="color:Red;font-weight:bold;">(.*?)</span>',1);
							if Result='' then
								Result:=Html.RegExp('_lblAfterSendStatus" class="left_align">(.*?)</span>',1);
							if Result='' then
								Result:='Ошибка подтверждения доставки';
						end;
						)
