classprops.pageurl:
eval(
					begin
						if SMS['Phone_tag']=1 then
							Stored['region']:='buryatia'
						else if SMS['Phone_tag']=2 then
							Stored['region']:='kaliningrad'
						else
							Stored['region']:='msk';
						Result:='http://www.mts.ru/live/smsxtra.aspx?region='+Stored['region'];
					end;
					)

classprops.postdata:
eval(
					var
						DT: TDateTime;
						PhoneDef: string;
					begin
						DT:=SMS['DeliveryTime'];
						if SMS['Delivery'] then
						begin	
							if (DT-Now)<5*OneMinute then
							begin
								DT:=DT+5*OneMinute;
							end;	
						end;
						DT:=AdjustTimeZone(DT,180,true);
						if SMS['Phone_Def']='495' then
							PhoneDef:='985'
						else PhoneDef:=SMS['Phone_Def'];	

						Stored['Hidden']:=DecodeFormElem(Html.RegExp('_TSM_CombinedScripts_=(.*?)" type',1));
						Stored['captchanewkey']:=Html.RegExp('id="LBD_VCT_live_smsxtra_ctl00_ctl00_ctl00_smssendcontrol_captchanew" value="(.*?)"',1);
						Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);
						Stored['EventValidation']:=Html.RegExp('id="__EVENTVALIDATION" value="(.*?)"',1);
						
						
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$RadScriptManager1','ctl00$ctl00$ctl00$SMSSendControl$ctl00$ctl00$ctl00$SMSSendControl$updatePanelPanel|ctl00$ctl00$ctl00$SMSSendControl$btnSend');
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadScriptManager1_HiddenField',Stored['Hidden']);
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadFormDecorator1_ClientState','');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ddPrefix',PhoneDef);
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ddPrefix_ClientState','{"logEntries":[],"value":"'+PhoneDef+'","text":"'+PhoneDef+'","enabled":true}');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$tbPhoneNumber',SMS['Phone_Number']);
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$tbMessage',SMS['Text']);
						if SMS['Flash'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$chbFlashSMS','on');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$tbCode',SMS['Code']);
						if SMS['Delivery'] then
						begin
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$chbForCalendar','on');
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ctl01',FormatDateTime('dd',DT));
						end;	
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ctl01_ClientState','{"logEntries":[],"value":"'+FormatDateTime('d',DT)+'","text":"'+FormatDateTime('d',DT)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ctl02',GetMonthLongRus(DT,false,true));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ctl02_ClientState','{"logEntries":[],"value":"'+FormatDateTime('m',DT)+'","text":"'+GetMonthLongRus(DT,false,true)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ctl03',FormatDateTime('yyyy',DT));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ctl03_ClientState','{"logEntries":[],"value":"'+FormatDateTime('yyyy',DT)+'","text":"'+FormatDateTime('yyyy',DT)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ctl04',FormatDateTime('hh',DT));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ctl04_ClientState','{"logEntries":[],"value":"'+FormatDateTime('h',DT)+'","text":"'+FormatDateTime('h',DT)+'","enabled":false}');
						if SMS['Delivery'] then
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$ctl06',FormatDateTime('nn',DT));
						PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_ctl06_ClientState','{"logEntries":[],"value":"'+FormatDateTime('n',DT)+'","text":"'+FormatDateTime('n',DT)+'","enabled":false}');
						PostData.Add('__EVENTTARGET','');
						PostData.Add('__EVENTARGUMENT','');
						PostData.Add('__VIEWSTATE',Stored['ViewState']);
						PostData.Add('__EVENTVALIDATION',Stored['EventValidation']);
						PostData.Add('LBD_VCT_live_smsxtra_ctl00_ctl00_ctl00_smssendcontrol_captchanew',Stored['captchanewkey']);
						PostData.Add('__ASYNCPOST','true');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$btnSend.x','78');
						PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$btnSend.y','14');
						PostData.Add('RadAJAXControlID','ctl00_ctl00_ctl00_SMSSendControl_updatePanel');
					end;
					)

classprops.error:
eval(begin
						if HTML.ExistsStr('smsExtraRedirect.htm') then
							Result:='SMS-сообщение можно отправлять не чаще одного раза в 30 секунд'
						else 	
							Result:=HTML.RegExp('class="ERROR">(.*?)</span>',1);
						if (Result='') and (Html.ExistsStr('ctl00_ctl00_ctl00_SMSSendControl_pnlForm')) then
							Result:='Превышение лимита отправки, попробуйте позже';
					end;		
							
					)

classprops.cd_postdata:
eval(
						var
							Viewstate: string;
							Eventvalid: string;
						begin
							Viewstate:=Html.RegExp('__VIEWSTATE\|(.*?)\|',1);
							Eventvalid:=Html.RegExp('__EVENTVALIDATION\|(.*?)\|',1);
							PostData.Add('ctl00$ctl00$ctl00$SMSSendControl$RadScriptManager1','ctl00$ctl00$ctl00$SMSSendControl$ctl00$ctl00$ctl00$SMSSendControl$updatePanelPanel|ctl00$ctl00$ctl00$SMSSendControl$lblStatusLink');
							PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadScriptManager1_HiddenField',Stored['Hidden']);
							PostData.Add('LBD_VCT_live_smsxtra_ctl00_ctl00_ctl00_smssendcontrol_captchanew',Stored['captchanewkey']);
							PostData.Add('ctl00_ctl00_ctl00_SMSSendControl_RadFormDecorator1_ClientState','');
							PostData.Add('__EVENTTARGET','ctl00$ctl00$ctl00$SMSSendControl$lblStatusLink');
							PostData.Add('__EVENTARGUMENT','');
							PostData.Add('__VIEWSTATE',Viewstate);
							PostData.Add('__EVENTVALIDATION',Eventvalid);
							PostData.Add('__ASYNCPOST','true');
							PostData.Add('RadAJAXControlID','ctl00_ctl00_ctl00_SMSSendControl_updatePanel');
						end;
						)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('Сообщение находится в очереди') or
									HTML.ExistsStr('ожидаем') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Cообщение доставлено');	
							end;
						)

classprops.cd_error:
eval(
						begin
							Result:=HTML.RegExp('cтатус сообщения:<b/>(.*?)<b/>',1);
							if Result='' then
								Result:=HTML.RegExp('style="color:Red;">(.*?)</span>',1);
							if Result='' then
								Result:=Html.RegExp('<span id="ctl00_ctl00_ctl00_SMSSendControl_ResultId" class="left_align"><b/>(.*?)<b/>',1);
							if Result='' then
								Result:='Ошибка подтверждения доставки';
						end;
						)
