classprops.pageurl:
eval(
					begin
						if SMS['Phone_tag']=1 then
						begin
							Stored['siteurl']:='http://sms.buryatia.mts.ru/';
							Stored['timeadd']:=-4;
						end	
						else if SMS['Phone_tag']=2 then
						begin
							Stored['siteurl']:='http://sms.kaliningrad.mts.ru/';
							Stored['timeadd']:=2;
						end	
						else
						begin
							Stored['siteurl']:='http://www.mts.ru/messaging/sendsms/';
							Stored['timeadd']:=0;
						end;
						Result:=Stored['siteurl'];
					end;
					)

classprops.postdata:
eval(
					var
						DT: TDateTime;
						PhoneDef: string;
					begin
						DT:=SMS['DeliveryTime'];
						if SMS['Delivery'] then
						begin	
							if (DT-Now)<5*OneMinute then
							begin
								DT:=DT+5*OneMinute;
							end;	
						end;
						DT:=AdjustTimeZone(DT,180,true);
						if not SMS['Delivery'] then
							DT:=DT+Stored['timeadd']*OneHour;
						if SMS['Phone_Def']='495' then
							PhoneDef:='985'
						else PhoneDef:=SMS['Phone_Def'];

						Stored['captchanewkey']:=Html.RegExp('smssendingcontrol_captchanew" value="(.*?)"',1);
						Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);
						Stored['RegionSiteId']:=Html.RegExp('name="RegionSiteId" value="(.*?)"',1);
						Stored['TopMenuParentId']:=Html.RegExp('name="TopMenuParentId" value="(.*?)"',1);
						Stored['BannerId']:=Html.RegExp('name="BannerId" value="(.*?)"',1);
						
						Stored['ParamPrefix']:=Html.StrBetweenRev('name="','$ddPrefix')+'$';
						Stored['ParamPrefix1']:=Html.StrBetweenRev('id="','_RadFormDecorator1')+'_';
						
						Stored['scriptManagerId_TSM']:=Html.RegExp('_TSM_CombinedScripts_=(.*?)"',1);


						PostData.Add('ctl00$ctl03$ctl00$scriptManagerId',Stored['ParamPrefix']+Stored['ParamPrefix']+'updatePanelPanel|'+Stored['ParamPrefix']+'btnSend');
						PostData.Add('ctl00_ctl03_ctl00_scriptManagerId_TSM',Stored['scriptManagerId_TSM']);
						PostData.Add('__EVENTTARGET','');
						PostData.Add('__EVENTARGUMENT','');
						PostData.Add('LBD_VCT_live_smssending_'+Stored['ParamPrefix1']+ 'captchanew',Stored['captchanewkey']);
						PostData.Add('__VIEWSTATE',Stored['ViewState']);
						PostData.Add('ctl00_ctl03_ctl00_rfdFormDecorator_ClientState','');
						PostData.Add(Stored['ParamPrefix1'] + 'RadFormDecorator1_ClientState','');
						PostData.Add(Stored['ParamPrefix'] + 'ddPrefix',PhoneDef);
						PostData.Add(Stored['ParamPrefix1'] + 'ddPrefix_ClientState','{"logEntries":[],"value":"'+PhoneDef+'","text":"'+PhoneDef+'","enabled":true}');
						PostData.Add(Stored['ParamPrefix'] + 'tbPhoneNumber',SMS['Phone_Number']);
						PostData.Add(Stored['ParamPrefix'] + 'tbMessage',SMS['Text']);
						PostData.Add(Stored['ParamPrefix'] + 'tbCode',SMS['Code']);
						if SMS['Delivery'] then
							PostData.Add(Stored['ParamPrefix'] + 'chbForCalendar','on');
						PostData.Add(Stored['ParamPrefix'] + 'ctl01',FormatDateTime('dd',DT));
						PostData.Add(Stored['ParamPrefix1'] + 'ctl01_ClientState','{"logEntries":[],"value":"'+FormatDateTime('d',DT)+'","text":"'+FormatDateTime('dd',DT)+'","enabled":false}');
						PostData.Add(Stored['ParamPrefix'] + 'ctl02',GetMonthLongRus(DT,false,true));
						PostData.Add(Stored['ParamPrefix1'] + 'ctl02_ClientState','{"logEntries":[],"value":"'+FormatDateTime('m',DT)+'","text":"'+GetMonthLongRus(DT,false,true)+'","enabled":false}');
						PostData.Add(Stored['ParamPrefix'] + 'ctl03',FormatDateTime('yyyy',DT));
						PostData.Add(Stored['ParamPrefix1'] + 'ctl03_ClientState','{"logEntries":[],"value":"'+FormatDateTime('yyyy',DT)+'","text":"'+FormatDateTime('yyyy',DT)+'","enabled":false}');
						PostData.Add(Stored['ParamPrefix'] + 'ctl04',FormatDateTime('hh',DT));
						PostData.Add(Stored['ParamPrefix1'] + 'ctl04_ClientState','{"logEntries":[],"value":"'+FormatDateTime('h',DT)+'","text":"'+FormatDateTime('hh',DT)+'","enabled":false}');
						PostData.Add(Stored['ParamPrefix'] + 'ctl06',FormatDateTime('nn',DT));
						PostData.Add(Stored['ParamPrefix1'] + 'ctl06_ClientState','{"logEntries":[],"value":"'+FormatDateTime('n',DT)+'","text":"'+FormatDateTime('nn',DT)+'","enabled":false}');
						PostData.Add('RegionSiteId',Stored['RegionSiteId']);
						PostData.Add('TopMenuParentId',Stored['TopMenuParentId']);
						PostData.Add('BannerId',Stored['BannerId']);
						PostData.Add('__ASYNCPOST','true');
						PostData.Add(Stored['ParamPrefix'] + 'btnSend.x','68');
						PostData.Add(Stored['ParamPrefix'] + 'btnSend.y','10');
						PostData.Add('RadAJAXControlID',Stored['ParamPrefix1'] + 'updatePanel');
					end;	
					)

classprops.error:
eval(begin
						if HTML.ExistsStr('smsExtraRedirect.htm') then
							Result:='SMS-сообщение можно отправлять не чаще одного раза в 30 секунд'
						else 	
							Result:=HTML.RegExp('class="ERROR">(.*?)</span>',1);
						if (Result='') and (Html.ExistsStr('ctl00_ctl00_ctl00_SMSSendControl_pnlForm')) then
							Result:='Превышение лимита отправки, попробуйте позже';
					end;		
							
					)

classprops.cd_postdata:
eval(
						var
							Viewstate: string;
							Eventvalid: string;
						begin
							Viewstate:=Html.RegExp('__VIEWSTATE\|(.*?)\|',1);
							Eventvalid:=Html.RegExp('__EVENTVALIDATION\|(.*?)\|',1);

							PostData.Add('ctl00$ctl03$ctl00$scriptManagerId',Stored['ParamPrefix']+Stored['ParamPrefix']+'updatePanelPanel|'+Stored['ParamPrefix']+'lbCheckMessageStatus');
							PostData.Add('ctl00_ctl03_ctl00_scriptManagerId_TSM',Stored['scriptManagerId_TSM']);
							PostData.Add('LBD_VCT_live_smssending_'+Stored['ParamPrefix1']+ 'captchanew',Stored['captchanewkey']);
							PostData.Add('ctl00_ctl03_ctl00_rfdFormDecorator_ClientState','');
							PostData.Add(Stored['ParamPrefix1'] + 'RadFormDecorator1_ClientState','');
							PostData.Add(Stored['ParamPrefix'] + 'hfMessageID',Html.RegExp('SmsSendingControl_hfMessageID" value="(.*?)"',1));
							PostData.Add('RegionSiteId',Stored['RegionSiteId']);
							PostData.Add('TopMenuParentId',Stored['TopMenuParentId']);
							PostData.Add('BannerId',Stored['BannerId']);

							PostData.Add('__EVENTTARGET',Stored['ParamPrefix'] + 'lbCheckMessageStatus');
							PostData.Add('__EVENTARGUMENT','');
							PostData.Add('__VIEWSTATE',ViewState);
							PostData.Add('__ASYNCPOST','true');
							PostData.Add('RadAJAXControlID',Stored['ParamPrefix1'] + 'updatePanel');
						end;
						)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('Сообщение находится в очереди') or
									HTML.ExistsStr('ожидаем') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Сообщение отправлено') or HTML.ExistsStr('Сообщение доставлено');	
							end;
						)

classprops.cd_error:
eval(
						begin
							Result:=HTML.RegExp('cтатус сообщения:<b/>(.*?)<b/>',1);
							if Result='' then
								Result:=HTML.RegExp('style="color:Red;">(.*?)</span>',1);
							if Result='' then
								Result:=Html.RegExp('<span id="ctl00_ctl00_ctl00_SMSSendControl_ResultId" class="left_align"><b/>(.*?)<b/>',1);
							if Result='' then
								Result:=Html.RegExp('="color:Red;font-weight:bold;">(.*?)</span>',1);
							if Result='' then
								Result:=Html.RegExp('_lblAfterSendStatus" class="left_align">(.*?)</span>',1);
							if Result='' then
								Result:='Ошибка подтверждения доставки';
						end;
						)
