classprops.proccessimage:
eval(
			var
				i,cnt, p: integer;
				Items, PStr: string;
			begin
				Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);

				HTTP.HTTPMethod('POST','http://www.mts.ru/Captcha/CaptchaInfo.svc/json/GetCaptchaInfo');
				Items:=Html.StrBetween('Items":[',']');
				p:=Pos(',',Items);
				cnt:=0;
				while (p>0) or (Items<>'') do
				begin
					cnt:=cnt+1;
					if p>0 then
					begin
						PStr:=Copy(Items,1,p-1);
						Delete(Items,1,p);
					end else
					begin
						PStr:=Items;
						Items:='';
					end;	
					Stored['CaptchaImg'+VarToStr(cnt)]:=StrBetween(PStr,'Id":"','"');


					p:=Pos(',',Items);
				end;

				Stored['QuestionText']:=CleanMessageText(Html.StrBetween('QuestionText":"','"'));
				
				Img.SetSize(cnt*50,50);
				
				for i:=1 to cnt do
				begin
					HTTP.HTTPMethod('GET','http://www.mts.ru/Captcha/image.ashx?CaptchaItemId='+Stored['CaptchaImg'+VarToStr(i)]);
					Img.AssignSourceImage($FFFFFF);
					Img.CopyRect((i-1)*50,0,Bounds(0,0,50,50));
				end;
				
				
			end;
		)

classprops.postdata:
eval(
					var
						DT: TDateTime;
						PhoneDef: string;
						Code, FinalCode: string;
						i: integer;
					begin
						DT:=SMS['DeliveryTime'];
						if SMS['Delivery'] then
						begin	
							if (DT-Now)<5*OneMinute then
							begin
								DT:=DT+5*OneMinute;
							end;	
						end;
						DT:=AdjustTimeZone(DT,180,true);
						if SMS['Phone_Def']='495' then
							PhoneDef:='985'
						else PhoneDef:=SMS['Phone_Def'];

						Code:=SMS['Code'];
						for i:=1 to Length(Code) do
						begin
							if Length(FinalCode)>0 then
								FinalCode:=FinalCode+',';
							FinalCode:=FinalCode+Stored['CaptchaImg'+Code[i]];
						end;
						
