classprops.mu_postdata:
eval(
				var
					MC: TMultimediaContent;
				begin
					MC:=MMS['Multimedia'];
					PostData.Add('Filename',MC['FileName']);
					PostData.Add('UPLOADER_ID',Html.RegExp('''UPLOADER_ID'' : ''(.*?)''',1));
					PostData.AddData('FUDATA',MC);
					PostData.Add('Upload','Submit Query');
				end;
				)

classprops.postdata:
eval(
						begin

						Stored['captchanewkey']:=Html.RegExp('mmssendingcontrol_captchanew" value="(.*?)"',1);
						Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);

						Stored['ParamPrefix']:=Html.StrBetweenRev('name="','$ddPrefix')+'$';
						Stored['ParamPrefix1']:=Html.StrBetweenRev('sendmms_','_captchanew')+'_';
						Stored['ParamPrefix2']:=Html.StrBetweenRev('"','_scriptManagerId_TSM"')+'_';
						Stored['ParamPrefix3']:=Html.StrBetweenRev('''','$scriptManagerId'',');
						
						Stored['scriptManagerId_TSM']:=Html.RegExp('_TSM_CombinedScripts_=(.*?)"',1);


						PostData.Add(Stored['ParamPrefix3']+'$scriptManagerId',Stored['ParamPrefix'] + 
																			Stored['ParamPrefix'] + 
																			'updatePanelPanel|' + 
																			Stored['ParamPrefix']+'btnSend');
						PostData.Add(Stored['ParamPrefix2']+'_scriptManagerId_TSM',Stored['scriptManagerId_TSM']);
						PostData.Add('LBD_VCT_live_nd_sendmms_'+Stored['ParamPrefix1']+ 'captchanew',Stored['captchanewkey']);
						PostData.Add('__EVENTTARGET','');
						PostData.Add('__EVENTARGUMENT','');
						PostData.Add('__VIEWSTATE',Stored['ViewState']);
						PostData.Add(Stored['ParamPrefix2']+ '_rfdFormDecorator_ClientState','');
						PostData.Add(Stored['ParamPrefix'] + 'hfCurrentID','');
						PostData.Add(Stored['ParamPrefix'] + 'hfWasUploadedDelited','');
						PostData.Add(Stored['ParamPrefix'] + 'ddPrefix',MMS['Phone_Def']);
						PostData.Add(Stored['ParamPrefix1'] + 'ddPrefix_ClientState','{"logEntries":[],"value":"'+MMS['Phone_Def']+'","text":"'+MMS['Phone_Def']+'","enabled":true}');
						PostData.Add(Stored['ParamPrefix'] + 'tbPhoneNumber',MMS['Phone_Number']);
						PostData.Add(Stored['ParamPrefix'] + 'ddlTopic','Выберите заголовок...');
						PostData.Add(Stored['ParamPrefix1'] + 'ddlTopic_ClientState','{"logEntries":[],"value":"0","text":"Выберите заголовок...","enabled":true}');
						PostData.Add(Stored['ParamPrefix'] + 'tbTopic',MMS['Subject']);
						PostData.Add(Stored['ParamPrefix'] + 'tbMessage',MMS['Text']);
						PostData.Add(Stored['ParamPrefix'] + 'tbCode',MMS['Code']);
						PostData.Add('__ASYNCPOST','true');
						PostData.Add(Stored['ParamPrefix'] + 'btnSend.x','156');
						PostData.Add(Stored['ParamPrefix'] + 'btnSend.y','15');
						PostData.Add('RadAJAXControlID',Stored['ParamPrefix1'] + 'updatePanel');
						
					end;
					)

classprops.cd_postdata:
eval(
						var
							Viewstate: string;
							Eventvalid: string;
						begin
							Viewstate:=Html.RegExp('__VIEWSTATE\|(.*?)\|',1);
							Eventvalid:=Html.RegExp('__EVENTVALIDATION\|(.*?)\|',1);

							PostData.Add(Stored['ParamPrefix3']+'$scriptManagerId',Stored['ParamPrefix'] + 
																			Stored['ParamPrefix'] + 
																			'updatePanelPanel|' + 
																			Stored['ParamPrefix']+'btnSend');
							PostData.Add(Stored['ParamPrefix2']+'_scriptManagerId_TSM',Stored['scriptManagerId_TSM']);
							PostData.Add('LBD_VCT_live_nd_sendmms_'+Stored['ParamPrefix1']+ 'captchanew',Stored['captchanewkey']);
							PostData.Add(Stored['ParamPrefix2']+ '_rfdFormDecorator_ClientState','');
							PostData.Add('__EVENTTARGET',Stored['ParamPrefix'] + 'StatusLink');
							PostData.Add('__EVENTARGUMENT','');
							PostData.Add('__VIEWSTATE',Viewstate);
							PostData.Add('__ASYNCPOST','true');
							PostData.Add('RadAJAXControlID',Stored['ParamPrefix1'] + 'updatePanel');
						end;	
					)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('В очереди') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Доставлено');	
							end;
						)
