classprops.proccessimage:
eval(
					var
						MC: TMultimediaContent;
						DT: TDateTime;
						PhoneDef: string;
						FinalCode: string;
						i, cnt, p: integer;
						Code, MTSCode, Items, PStr: string;
						JS: TJSEval;
			begin
				Stored['ErrorText']:='';
				if MMS['CaptchaIteration']=1 then
				begin
					Stored['ViewState']:=Html.RegExp('id="__VIEWSTATE" value="(.*?)"',1);
					Stored['InputPrefix']:=Html.StrBetweenRev('name="','$tbRecipientPhoneNumber'); //ctl00$ctl00$ctl00$ctl03$ctl00$ctl07$ctl01$ctl02$ctl00
					Stored['InputPrefix2']:=Html.StrBetweenRev('name="','_scriptManagerId_TSM'); //ctl00_ctl00_ctl00_ctl03_ctl00_ctl07
					Stored['InputPrefix3']:=Html.RegExp('PageRequestManager\._initialize\(''(.*?)\$scriptManagerId''',1); //ctl00$ctl00$ctl00$ctl03$ctl00$ctl07
					Stored['InputPrefix6']:=Html.StrBetweenRev('name="','$sf$TextBoxMessage'); //ctl00$ctl00$ctl00$ctl03$ctl00$ctl06$ctl01$ctl03
					Stored['InputPrefix7']:=Html.StrBetweenRev('"', '$ctrlCaptcha$ClientState'); 					Stored['InputTSMValue']:=DecodeFormElem(Html.RegExp('_TSM_CombinedScripts_=(.*?)"',1)); //Value _scriptManagerId_TSM
					Stored['Uploader_ID']:=Html.RegExp('''UPLOADER_ID'' : ''(.*?)''',1);

					HTTP.HTTPMethod('POST','https://sendmms.ssl.mts.ru/Captcha/CaptchaInfo.svc/json/GetCaptchaInfo');
					JS:=TJSEval.Create;

					MTSCode:=Html.RegExp('VCode":"(.*)"}$',1);
					MTSCode:=ReplaceStr(MTSCode, '\"','"');

					//Добавялем свой код, эмулируем jQuery
					Code:='' + 	
'var inputs = [];' + sLineBreak +
'function form() {' + sLineBreak +
'	return {' + sLineBreak +
'		append: function(obj){' + sLineBreak +
'		}' + sLineBreak +
'	}	' + sLineBreak +
'};' + sLineBreak +
'function input(){' + sLineBreak +
'	var new_input = {' + sLineBreak +
'		classes: [],' + sLineBreak +
'		attrs: [],' + sLineBreak +
'		name: "",' + sLineBreak +
'		val: "",' + sLineBreak +
'		attr: function(name, value) {' + sLineBreak +
'			if (name!="name")' + sLineBreak +
'				this.attrs[name] = value;' + sLineBreak +
'			else' + sLineBreak +
'				this.name = value; 	' + sLineBreak +
'			return this;' + sLineBreak +
'		},' + sLineBreak +
'		addClass: function(classname) {' + sLineBreak +
'			this.classes.push(classname);' + sLineBreak +
'			return this; ' + sLineBreak +
'		},' + sLineBreak +
'		val: function(value) {' + sLineBreak +
'			this.val = value;' + sLineBreak +
'			return this;			' + sLineBreak +
'		},' + sLineBreak +
'		remove: function() {' + sLineBreak +
'			for(var i=0;i<inputs.length;i++) {' + sLineBreak +
'				if (inputs[i]==this) {' + sLineBreak +
'					inputs.splice(i,1);' + sLineBreak +
'					break;' + sLineBreak +
'				}' + sLineBreak +
'			}' + sLineBreak +
'		}' + sLineBreak +
'	};' + sLineBreak +
'	inputs.push(new_input);' + sLineBreak +
'	return new_input;' + sLineBreak +
'		' + sLineBreak +
'	' + sLineBreak +
'}' + sLineBreak +
'' + sLineBreak +
'function search_byselector(selector) {' + sLineBreak +
'	var className="";' + sLineBreak +
'	var AttrName="";' + sLineBreak +
'	var AttrValue="";' + sLineBreak +
'	//Извлекаем класс' + sLineBreak +
'	var p = selector.indexOf(".");' + sLineBreak +
'	if (p>=0) {' + sLineBreak +
'		className = selector.substring(p+1);' + sLineBreak +
'	}' + sLineBreak +
'	//Извлекаем аттрибут' + sLineBreak +
'	var reg = new RegExp("\\[([\\w\\d_]+)=\"([\\w\\d_]+)\"\\]","g");' + sLineBreak +
'	var matches = reg.exec(selector);' + sLineBreak +
'	while (matches!=null) {' + sLineBreak +
'		if (matches[1]!="type") {' + sLineBreak +
'			AttrName=matches[1];' + sLineBreak +
'			AttrValue=matches[2];' + sLineBreak +
'		}	' + sLineBreak +
'		matches = reg.exec(selector);' + sLineBreak +
'	}' + sLineBreak +
'	var result = null;' + sLineBreak +
'	//Ищем по классу или аттрибуту' + sLineBreak +
'	for (var i=0;i<inputs.length;i++) {' + sLineBreak +
'		if (className!="") {' + sLineBreak +
'			for (var j=0;j<inputs[i].classes.length;j++) {' + sLineBreak +
'				if (className==inputs[i].classes[j]) {' + sLineBreak +
'					result = inputs[i];' + sLineBreak +
'					break;' + sLineBreak +
'				}' + sLineBreak +
'			}' + sLineBreak +
'		}' + sLineBreak +
'		if ((AttrName!="") && (AttrValue!="")) {' + sLineBreak +
'			if (inputs[i].attrs[AttrName]==AttrValue) {' + sLineBreak +
'				result = inputs[i];' + sLineBreak +
'			}' + sLineBreak +
'		}' + sLineBreak +
'	}' + sLineBreak +
'	if (result!=null)' + sLineBreak +
'		return result;' + sLineBreak +
'	else' + sLineBreak +
'		return new input();	' + sLineBreak +
'}' + sLineBreak +
'' + sLineBreak +
'function jQuery(selector, scope) {' + sLineBreak +
'	switch (selector.toLowerCase()) {' + sLineBreak +
'		case "form":' + sLineBreak +
'			return new form();' + sLineBreak +
'			break;' + sLineBreak +
'		case "<input\/>":' + sLineBreak +
'			return new input();' + sLineBreak +
'			break;' + sLineBreak +
'		default:' + sLineBreak +
'			return search_byselector(selector);' + sLineBreak +
'			break;		' + sLineBreak +
'	}' + sLineBreak +
'}'+ sLineBreak;
				Code:=Code+MTSCode;
				Code:=Code+sLineBreak + 
'var r = "";' + sLineBreak +
'for (var i=0;i<inputs.length;i++) {' + sLineBreak +
'	r = r + "&amp;" + inputs[i].name + "=" + inputs[i].val;' + sLineBreak +
'}' + sLineBreak + 
JS.ResultName+'.SetValue("hidden",r);' + sLineBreak;

					JS.Exec(Code);
					Stored['HiddenInputs']:=JS.GetResultValue('hidden');
					//Получаем изображения
					Items:=Html.StrBetween('Items":[',']');
					p:=Pos(',',Items);
					cnt:=0;
					while (p>0) or (Items<>'') do
					begin
						cnt:=cnt+1;
						if p>0 then
						begin
							PStr:=Copy(Items,1,p-1);
							Delete(Items,1,p);
						end else
						begin
							PStr:=Items;
							Items:='';
						end;	
						Stored['CaptchaImg'+VarToStr(cnt)]:=StrBetween(PStr,'Id":"','"');
						p:=Pos(',',Items);
					end;

					Stored['ImageDesc']:=CleanMessageText(Html.StrBetween('QuestionText":"','"'));
					
					Img.SetSize(cnt*50,50);
					
					for i:=1 to cnt do
					begin
						HTTP.HTTPMethod('GET','https://sendmms.ssl.mts.ru/Captcha/image.ashx?CaptchaItemId='+Stored['CaptchaImg'+VarToStr(i)]);
						Img.AssignSourceImage($FFFFFF);
						Img.CopyRect((i-1)*50,0,Bounds(0,0,50,50));
					end;				

					
				end;
				if MMS['CaptchaIteration']=2 then
				begin
					//Отправка мультимедиа
					MC:=MMS['Multimedia'];
					PostData.Multipart:=true;
					PostData.Add('Filename',MC['FileName']);
