					PostData.Add('Upload','Submit Query');
					HTTP.HTTPMethod('POST','https://sendmms.ssl.mts.ru/FlashUploader.axd');

					Code:=MMS['Code'];
					for i:=1 to Length(Code) do
					begin
						if Length(FinalCode)>0 then
							FinalCode:=FinalCode+',';
						FinalCode:=FinalCode+Stored['CaptchaImg'+Code[i]];
					end;

					PostData.Multipart:=false;


					PostData.Add(Stored['InputPrefix3'] + '$scriptManagerId',Stored['InputPrefix'] + '$' + Stored['InputPrefix'] + '$updatePanelPanel|' + Stored['InputPrefix'] + '$btnSend');
					PostData.Add(Stored['InputPrefix2'] + '_scriptManagerId_TSM',Stored['InputTSMValue']);
					PostData.Add('__EVENTTARGET',Stored['InputPrefix'] + '$btnSend');
					PostData.Add('__EVENTARGUMENT','');
					PostData.Add('__VIEWSTATE',Stored['ViewState']);
					PostData.Add('Region_Id','1826');
					PostData.Add('SiteSection_Id','1744');
					PostData.Add('ShowPersCabLink','False');
					PostData.Add('TopMenuParentId','801624');
					PostData.Add(Stored['InputPrefix'] + '$hfLastMMSMessageId','');
					PostData.Add(Stored['InputPrefix'] + '$hfChosenItemID','');
					PostData.Add(Stored['InputPrefix'] + '$hfChosenItemObjectID','');
					PostData.Add(Stored['InputPrefix'] + '$hfWasUploadedFileDeleted','');
					PostData.Add(Stored['InputPrefix'] + '$tbSenderPhoneNumber',Copy(MMS['YourPhone'],2,10));
					PostData.Add(Stored['InputPrefix'] + '$tbRecipientPhoneNumber',MMS['Phone_Def']+MMS['Phone_Number']);
					PostData.Add(Stored['InputPrefix'] + '$ddlTopic','0');
					PostData.Add(Stored['InputPrefix'] + '$tbTopic',MMS['Subject']);
					PostData.Add(Stored['InputPrefix'] + '$tbMessage',MMS['Text']);
					PostData.Add(Stored['InputPrefix'] + '$tbLen',Length(MMS['Text']));
					PostData.Add(Stored['InputPrefix'] + '$tbMax','1000');
					PostData.Add(Stored['InputPrefix7'] + '$ctrlCaptcha$ClientState',FinalCode);
					PostData.AddRawData(Stored['HiddenInputs']);
					PostData.Add('__ASYNCPOST','true');
					PostData.Add(Stored['InputPrefix'] + '$btnNext.x','114');
					PostData.Add(Stored['InputPrefix'] + '$btnNext.y','5');
		
					HTTP.CustomMime:='application/x-www-form-urlencoded; charset=utf-8';
					HTTP.Referer:='https://sendmms.ssl.mts.ru/';
					HTTP.HTTPMethod('POST','https://sendmms.ssl.mts.ru/');

					if Html.ExistsStr('Вы неправильно ответили на вопрос') then
						Stored['ErrorText']:='Вы неправильно ответили на вопрос! Попробуйте ответить на новый вопрос';
					
					Stored['ViewState']:=Html.RegExp('hiddenField\|__VIEWSTATE\|(.*?)\|',1);
					Img.SetSize(250,60);
					Img.TextOut(0,0,'На номер +' + MMS['YourPhone']);
					Img.TextOut(0,20,'выслан код подтверждения.');
					Img.TextOut(0,40,'Пожалуйста, введите его.');
				end;

			end;
		)

