classprops.postdata:
eval(
						var
							DT: TDateTime;
							Prefix: string;
						begin
							DT:=AdjustTimeZone(SMS['DeliveryTime'],180,true);
							if SMS['Phone_Def']='812' then
								Prefix:='921'
							else if SMS['Phone_Def']='495' then
								Prefix:='925'
							else
								Prefix:=SMS['Phone_Def'];
							PostData.Add('codekey',HTML.RegExp('name="codekey"   value="(.*?)"',1));
							PostData.Add('charcheck',HTML.RegExp('name="charcheck"   value="(.*?)"',1));
							PostData.Add('lang',HTML.RegExp('name="lang"   value="(.*?)"',1));
							PostData.Add('prefix',SMS['Phone_Country']+Prefix);
							PostData.Add('addr',SMS['Phone_Number']);
							PostData.Add('message',SMS['Text']);
							if SMS['Delivery'] then
								PostData.Add('send_after_flag','on');
							PostData.Add('send_day',FormatDateTime('d',DT));
							PostData.Add('send_month',FormatDateTime('m',DT));
							PostData.Add('send_hour',FormatDateTime('h',DT));
							PostData.Add('send_minute',FormatDateTime('n',DT));
							PostData.Add('send_year',FormatDateTime('yyyy',DT));
							PostData.Add('codevalue',SMS['Code']);
						end;
					)

classprops.success:
eval(
					begin
						HTTP.HTTPMethod('GET','http://sendsms.megafon.ru/send/'+RegExp(HTTP['Location'],'send/(.*?)$',1)+'/');
						Result:=HTML.ExistsStr('Проверить статус');
						if Result then
							Stored['MsgID']:=Html.RegExp('"/send/status/(.*?)("|/)',1);
					end;)

classprops.error:
eval(begin
						if Pos('invalid_key',HTTP['URL'])>0 then
							Result:='Неверно введен код'
						else	
							Result:=HTML.RegExp('<h1 class="error">(.*?)</h1>',1);
					end;		
		)

classprops.cd_success:
eval(begin
							if Html.ExistsStr('Сообщение в процессе доставки') then
								Result:='WAIT'
							else Result:=HTML.ExistsStr('Сообщение доставлено');	
						end;
					)

classprops.cd_error:
eval(begin
						Result:=Html.RegExp('<label>состояние</label><p.*?>(.*?)</p>',1);
						if Result='' then
							Result:='Ошибка при проверке подтверждения доставки';
					end;)
