classprops.postdata:
eval(
			begin
				PostData.Add('PHPSESSID',HTML.RegExp('name="PHPSESSID" value="(.*?)"',1));
				PostData.Add('id',HTML.RegExp('name=''id'' value=''(.*?)''',1));
				PostData.Add('action','submit');
				PostData.Add('Prefix','');
				PostData.Add('Address','+'+SMS['Phone']);
				PostData.Add('code',SMS['Code']);
				PostData.Add('Message',SMS['Text']);
				PostData.Add('Submit','Отправить');
				if (SMS['Flash']) then
					PostData.Add('Flashsm','');
			end;
		)

classprops.cd_postdata:
eval(
						begin
							PostData.Add('id',Html.RegExp('name=''id'' value=''(.*?)''',1));
							PostData.Add('action','query');
							PostData.Add('Identity',Html.RegExp('name=''Identity'' value=''(.*?)''',1));
							PostData.Add('id',Html.RegExp('name=''id'' value=''(.*?)''',1));
							PostData.Add('action','query');
							PostData.Add('Identity',Html.RegExp('name=''Identity'' value=''(.*?)''',1));
							PostData.Add('Submit','Проверить');
						end;
					)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('идет попытка доставки') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('доставлено');
							end;
						)

classprops.cd_error:
eval(
					begin
						Result:=HTML.RegExp('Текущий статус сообщения: <B>(.*?)</B>',1);
						if Result='' then
							Result:='Ошибка подтверждения доставки';
					end;)
