classprops.postdata:
eval(
			var
				PhoneDef: string;
			begin
				if SMS['Phone_Def']='495' then
					PhoneDef:='925'
				else
					PhoneDef:=SMS['Phone_Def'];
				Result:='';
				Result:=Result+'codekey='+ HTML.RegExp('name="codekey".*?value="(.*?)"',1);
				Result:=Result+'&charcheck='+ConvertToEncoding('йцукен','UTF-8');
				Result:=Result+'&codevalue='+SMS['Code'];
				Result:=Result+'&prefix='+SMS['Phone_Country']+PhoneDef;
				Result:=Result+'&addr='+SMS['Phone_Number'];
				Result:=Result+'&translitarate=on';
				Result:=Result+'&message='+ConvertToEncoding(SMS['Text'],'UTF-8');
				Result:=Result+'&messlen=150';
				Result:=Result+'&go='+ConvertToEncoding('Отправить сейчас','UTF-8');
				Result:=Result+'&send_day=1';
				Result:=Result+'&send_month=2';
				Result:=Result+'&send_hour=1';
				Result:=Result+'&send_minute=1';
				Result:=Result+'&send_year=2009';
			end;
		)

classprops.error:
eval(
				begin
					if Pos('/invalid_key',HTTP['URL'])>0 then
						Result:='Неверный код'
					else
						Result:=Html.RegExp('li class="current">(.*?)</li>',1);
					if Result='' then
						Result:='Неизвестная ошибка';
				end;		
			)

classprops.cd_success:
eval(
							begin
								if HTML.ExistsStr('Сообщение в процессе доставки') then
									Result:='WAIT'
								else Result:=HTML.ExistsStr('Сообщение доставлено');
							end;
						)
