classprops.postdata:
eval(
			begin
				PostData.Add('cc',Html.RegExp('type=hidden value=(.*?) ',1));
				PostData.Add('prefix',SMS['Phone_Def']);
				PostData.Add('Addr',SMS['Phone_Number']);
				PostData.Add('txtSM',SMS['Text']);
				PostData.Add('messlen',SMS['LeaveSymbols']);
				if SMS['Delivery'] then
				begin
					PostData.Add('send_time','later');
					PostData.Add('later_day',FormatDateTime('dd',SMS['DeliveryTime']));
					PostData.Add('later_month',FormatDateTime('mm',SMS['DeliveryTime']));
					PostData.Add('later_year',FormatDateTime('yyyy',SMS['DeliveryTime']));
					PostData.Add('later_hour',FormatDateTime('hh',SMS['DeliveryTime']));
					PostData.Add('later_min',FormatDateTime('nn',SMS['DeliveryTime']));
				end
				else PostData.Add('send_time','now');
				PostData.Add('vixod',SMS['Code']);
				PostData.Add('BSendSms.x','63');
				PostData.Add('BSendSms.y','10');
			end;
		)

classprops.cd_success:
eval(
						begin
							if HTML.ExistsStr('Сообщение ожидает доставки') then
								Result:='WAIT'
							else Result:=HTML.ExistsStr('Сообщение доставлено адресату');
						end;	
						)
