classprops.postdata:
eval(
					begin
						PostData.Add('phone_prefix',SMS['Phone_Country'] + SMS['Phone_Def']);
						PostData.Add('phone_number',SMS['Phone_Number']);
						PostData.Add('message',SMS['Text']);
						PostData.Add('captcha_sid',Html.RegExp('name="captcha_sid" value="(.*?)"',1));
						PostData.Add('captcha_token',Html.RegExp('name="captcha_token" value="(.*?)"',1));
						PostData.Add('captcha_response',SMS['Code']);
						PostData.Add('op','');
						PostData.Add('form_build_id',Html.RegExp('name="form_build_id" value="(.*?)"',1));
						PostData.Add('form_id','websms_main_form');
					end;
					)

classprops.error:
eval(
			begin
				Result:=Html.RegExp('Сообщение об ошибке</h2>(.*?)</div>',1);
				if Pos('The answer you entered for the CAPTCHA was not correct.', Result)>0 then
					Result:='Неверно введен код подтверждения';
			end;
			)
