classprops.postdata:
eval(
					begin
						Cookies['1118_sms_text']:=SMS['Text'];
						Cookies['1118_sms_adress']:='+'+SMS['Phone'];
						Cookies['1118_sms_replyEmail']:='noreply@example.org';
						PostData.Add(Html.RegExp('<input type="hidden" name="(.*?)"',1),'1');
						PostData.Add('adress','+'+SMS['Phone']);
						PostData.Add('code',SMS['Code']);
						PostData.Add('replyEmail','noreply@example.org');
						PostData.Add('text',SMS['Text']);
						PostData.Add('send','ODESLAT');
					end;
					)
