classprops.postdata:
eval(
						var
							DT: Int64;
							i: integer;
							bTrans: boolean;
							s,s1: string;
						begin
							DT:=DateTimeToUnix(Now())*1000;
							bTrans:=IsAscii(SMS['Text']);
							s1:=Translit(SMS['Text']);
							for i:=1 to Length(s1) do
								s:=s+Iff(bTrans,s1[i],'&'+s1[i]+';');
							
							PostData.Add('sid3',Html.RegExp(' name="sid3" value="(.*?)"',1));
							PostData.Add('user_input_timestamp',VarToStr(DT));
							PostData.Add('user_input_0','_next');
							PostData.Add('user_input_1','0'+SMS['Phone_Def']);
							PostData.Add('user_input_2',SMS['Phone_Number']);
							PostData.Add('replaced_input_3',SMS['Text']);
							PostData.Add('user_input_3',s);
							PostData.Add('user_input_SecretImage',SMS['Code']);
						end;
					)

classprops.error:
eval(begin
						Result:=Html.RegExp('<div class="splashtext">(.*?)</div>',1);
						if Result='' then
							Result:='Ошибка отправки, возможно введен неверно код указанный на картинке';
					end;)
