						PostData.Add('ctl00$txtSearch','');
						PostData.Add('ctl00$pageContent$TextBox1',SMS['Code']);
						PostData.Add('ctl00$pageContent$TextBox2',Copy(SMS['Phone'],2,10));
						PostData.Add('ctl00$pageContent$TextBox3',SMS['Text']);
