classprops.postdata:
eval(
					begin
						PostData.Add('send','1');
						PostData.Add('sms',Html.RegExp('name="sms" value=''(.*?)''',1));
						PostData.Add('gp',Html.RegExp('name="gp" value=''(.*?)''',1));
						PostData.Add('_en',Html.RegExp('name="_en" value="(.*?)"',1));
						PostData.Add('_by',Html.RegExp('name="_by" value="(.*?)"',1));
						PostData.Add('nat',SMS['Phone_Def']);
						PostData.Add('phone',SMS['Phone_Number']);
						PostData.Add('smslang',iff(IsAscii(SMS['Text']),'lat','rus'));
						PostData.Add('SM',SMS['Text']);
						PostData.Add('num',SMS['LeaveSymbols']);
						PostData.Add('m1',SMS['Code']);
					end;			
		)
