classprops.postdata:
eval(
			var 
				PostStr: string;
				CountryStr: string;
				TextField: string;
				TokenCookieStr: string;
				TmpStr: string;
				Matches: TRegExpMatches;
				i,j: integer;
			begin
				TokenCookieStr := Html.RegExp('"token","(.*?)"', 1);
				Stored['CountryField'] := Html.RegExp('<strong>Country:.*?<select.*?name="(.*?)"', 1);
				Stored['TextField'] := Html.RegExp('<strong>Message:.*?<textarea.*?name="(.*?)"', 1);
				Stored['PhoneField'] := Html.RegExp('<strong>Mobile Number:.*?<input.*?name="(.*?)"', 1);
				Stored['CodeField'] := Html.RegExp('<strong>Verification Code:.*?<input.*?name="(.*?)"', 1);
				Stored['MsgLenField'] := 'msgLen';

				Matches := TRegExpMatches.Create;
				Html.RegExpAll('<input.*?>', Matches);
				if (Matches.MatchesCount >= 6) then
				begin
					Stored['csrfNameField'] := RegExp('name="(.*?)"', Matches.GetCapture(5, 0), 1);
					Stored['csrfNameValue'] := RegExp('value="(.*?)"', Matches.GetCapture(5, 0), 1);
					Stored['csrfTokenField'] := RegExp('name="(.*?)"', Matches.GetCapture(6, 0), 1);
					Stored['csrfTokenValue'] := RegExp('value="(.*?)"', Matches.GetCapture(6, 0), 1);
				end;

				if (SMS['Phone_Country'] = '7') and (Copy(SMS['Phone_Def'], 1, 1) = '9') then
					CountryStr := 'RU'
				else if (SMS['Phone_Country'] = '7') and (Copy(SMS['Phone_Def'], 1, 1) = '7') then
					CountryStr := 'KZ'
				else if (SMS['Phone_Country'] = '380') then
					CountryStr := 'UA';

				PostData.add('xajax', 'sel');
				PostData.add('xajaxr', VarToStr(DateTimeToUnix(Now())));
				PostData.Add('xajaxargs[]', CountryStr);
				HTTP.HTTPMethod('Post', 'http://afreesms.com/freesms/');

				PostStr := '<xjxquery><q>IL_IN_TAG=1';
				PostStr := PostStr + '&' + Stored['CountryField'] + '=' + CountryStr;
				PostStr := PostStr + '&' + Stored['PhoneField'] + '=' + SMS['Phone_Def'] + SMS['Phone_Number'];
				PostStr := PostStr + '&' + Stored['TextField'] + '=' + SMS['Text'];
				PostStr := PostStr + '&' + Stored['MsgLenField'] + '=' + VarToStr(SMS['MaxSymbols'] - Length(SMS['Text']));
				PostStr := PostStr + '&' + Stored['CodeField'] + '=' + SMS['Code'];
				PostStr := PostStr + '&' + Stored['csrfNameField'] + '=' + Stored['csrfNameValue'];
				PostStr := PostStr + '&' + Stored['csrfTokenField'] + '=' + Stored['csrfTokenValue'];
				PostStr := PostStr + '&IL_IN_TAG=1</q></xjxquery>';

				Http.Headers['Origin'] := 'http://afreesms.com';
				Http.Headers['Method'] := 'POST http://afreesms.com/freesms/ HTTP/1.1';
				Cookies['rd'] := 'afreesms.com';

				TokenCookieStr := ReplaceStr(TokenCookieStr, '\x', '');
				TokenCookieStr := DecodeHex(TokenCookieStr);
				Cookies['token'] := TokenCookieStr;

				PostData.add('xajax', 'processMsg');
				PostData.add('xajaxr', VarToStr(DateTimeToUnix(Now())));
				PostData.Add('xajaxargs[]', PostStr);
			end;
		)

classprops.error:
eval(
			begin
				if (Html.ExistsStr('Verification Code')) then
					Result := 'Проверочный код введен неверно'
				else	
					Result:=Html.RegExp('alt="close" />(.*?)\]', 1);
				if Result='' then
					Result := 'Неизвестная ошибка';
			end;
		)
